mapper.select:
SELECT * FROM tbl_newsComent
WHERE seqj = #{seqj} 
ORDER BY seqj DESC

mapper.update:
UPDATE tbl_newsComent SET
title = #{title, jdbcType=VARCHAR},
cont  = #{cont,  jdbcType=VARCHAR},
dates = #{dates, jdbcType=VARCHAR},
times = #{times, jdbcType=VARCHAR}
WHERE seq = #{seq, jdbcType=VARCHAR}
